mapper.insert:
insert into a_account_operation (
  gamer_id,
  operation_type,
  old_value,
  new_value,
  verification_code,
  initiation_date,
  completion_date,
  operation_status,
  expiration_date
)
values (
  #{gamerId},
  #{operationType};
  #{oldValue};
  #{newValue};
  #{verificationCode};
  #{initiationDate};
  #{completionDate};
  #{operationStatus};
  #{expirationDate};
)
